import React, { Component } from "react";
import { Row, Col } from "reactstrap";
import { Link } from 'react-router-dom';

import InvestBox from "./InvestBox";
class Leagues extends Component {
  constructor(props) {
    super(props);
  }
  componentDidMount() {
    this.props.getCompetitions();
  }
  render() {
    const { competitions } = this.props;
    return (
      <div className="invest-page page-body">
        <div className="container">
          <Row>
            <Col md={{ size: 10, offset: 1 }}>
              <h1>Football Leagues</h1>
              <div className="cards">
                {competitions.loading && (
                  <i className="fas fa-spinner fa-pulse" />
                )}
                {competitions.payload &&
                  competitions.payload.count > 0 &&
                  competitions.payload.competitions.map((item, index) => {
                    return (
                      <div className="card" key={index}>
                        <div className="card-img"><i classNName="fas fa-image"></i></div>
                        <div className="card-content">
                          <p><b>{'Name :'}</b>{item.name}</p>
                          <p><b>{'Area Name :'}</b>{item.area.name}</p>
                        </div>
                        <div className="card-actions">
                          <Link to={item.id}> Details</Link>
                        </div>
                      </div>
                    );
                  })}
              </div>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}

export default Leagues;
