import React, { Component ,Fragment } from "react";
import { Row, Col } from "reactstrap";
import _ from "lodash";
import LeagueCard from "./LeagueCard";

class LeaguesItem extends Component {
  constructor(props) {
    super(props);
  }
  componentDidMount() {
    const currentId = this.props.match.params.id;
    this.props.getLeaguesItem(currentId);
    this.props.getTeams(currentId);
  }
  render() {
    const { leaguesItem } = this.props;
    return (
      <div className="page-body">
        <div className="container">
          <Row>
            <Col md={{ size: 10, offset: 1 }}>
              <h1>Football Leagues</h1>
              {leaguesItem.loading ? (
                <i className="fas fa-spinner fa-pulse" />
              ) : !_.isEmpty(leaguesItem.payload) ? (
                <Fragment>
                  <h2>{leaguesItem.payload.name}</h2>
                  <div className="cards">
                    <LeagueCard item={leaguesItem.payload} actions={false}/>
                  </div>
                </Fragment>
              ) : "NOT FOUND"}

            </Col>
          </Row>
        </div>
      </div>
    );
  }
}

export default LeaguesItem;
