import React, { Component } from "react";
import { Row, Col } from "reactstrap";
import { Link } from "react-router-dom";

import InvestBox from "./InvestBox";
class Leagues extends Component {
  constructor(props) {
    super(props);
  }
  componentDidMount() {
    this.props.getCompetitions();
  }
  render() {
    const { competitions } = this.props;
    return (
      <div className="invest-page page-body">
        <div className="container">
          <Row>
            <Col md={{ size: 10, offset: 1 }}>
              <h1>Football Leagues</h1>
              <div className="cards">
                {competitions.loading && (
                  <i className="fas fa-spinner fa-pulse" />
                )}
                {competitions.payload &&
                  competitions.payload.count > 0 &&
                  competitions.payload.competitions.map((item, index) => {
                    return (
                      <div className="card" key={index}>
                        <div className="card-img">
                          {item.emblemUrl ? (
                            <img src={item.emblemUrl} title={item.name} />
                          ) : (
                            <i className="fas fa-image" />
                          )}
                        </div>
                        <div className="card-content">
                          <p>
                            <b>{"Name: "}</b>
                            {item.name}
                          </p>
                          <p>
                            <b>{"Area Name: "}</b>
                            {item.area.name}
                          </p>
                          <p>
                            <b>{"number of Games: "}</b>
                            {item.numberOfAvailableSeasons}
                          </p>
                        </div>
                        <div className="card-actions">
                          <Link to={`/leagues/${item.id}`}> Details</Link>
                        </div>
                      </div>
                    );
                  })}
              </div>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}

export default Leagues;
